import Card from '../components/main/Card';

import React, {useState, useEffect} from 'react'

interface pokemon {

    id: number,

    name: string

}

const Landing: React.FC = () => {

    const [loading, setIsLoading] = useState<boolean>(true)

    const [pokemons, setPokemons] = useState<pokemon[] | null>(null)

    const [error, setError] = useState<string| null>(null)

    useEffect(() => {

        if(!pokemons){

            const getPokemons = async() => {

                try{

                    const data = await loginRequest({email, password})

                    const {user, token} : {user:user, token:string} = data

                    saveUserDetailsToStorage(user.latest_code, token, user.name)

                    setIsLoading(false)

                    setMsg({msg: "Registration Successful and Redirecting now", color: "success"})

                    if(!user.interest){

                      history.push('/interests')

                    }
                    else{

                      history.push ('/home')

                    }

                }

                catch(err){

                    setIsLoading(false)

                    console.log(err.response)

                    //setMsg({msg: err.response.data.message, color: "danger"})

                }

            }

        }

        else{

            setIsLoading(true)

        }

    }, [])

    return (

            <main>
                <div className="services">

                    <div className="container">

                        <div className="text-center">

                            <h2>List of Available Pokemons</h2>

                            <div className="border-multiple">

                                <span className="first"></span>

                                <span className="second"></span>

                                <span className="third"></span>

                            </div>

                        </div>

                        <div className="mt-5">

                            <div className="row">

                                <Card name="Crochet" id={4} key={1}/>
                                <Card name="Crocodie" id={4} key={2}/>
                                <Card name="Crochet" id={4} key={3}/>
                                <Card name="Crochet" id={4} key={4}/>
                                <Card name="Crochet" id={4} key={5}/>

                            </div>

                            <nav aria-label="Page navigation">

                                    <ul className="pagination justify-content-center">

                                        <li className="page-item"><a className="page-link color-2" href="/" style={{color: "#ff7b79"}}>Previous</a></li>

                                        <li className="page-item"><a className="page-link" href="/" style={{color: "#ff7b79"}}>Next</a></li>

                                    </ul>

                            </nav>

                        </div>
                    </div>
                </div>

             </main>

    )

}

export default Landing
